import NewTaskForm from '../new-task-form/new-task-form';

export default class Task extends Component {

    
    constructor() {
        super();
        this.state = {
            status: 'active',
        }
        this.completeTask = () => {
            this.setState(() => {
                return {
                    status: 'completed'
                }
            })
        }
        this.editTask = () => {
            this.setState(() => {
                return {
                    status: 'editing'
                }
            })
